import React, { useState } from 'react';
import { Formik, Form, FormikHelpers } from 'formik';
import * as Yup from 'yup';
import MyTextField from './use';
import MyMaskField from './use.Mask';
import { motion } from 'framer-motion';
import Button from '@material-ui/core/Button';
import { CircularProgress } from 'material-ui';
import Snackbar from '@material-ui/core/Snackbar';
import Alert from '@material-ui/lab/Alert';
import { SnackbarProvider, VariantType, useSnackbar } from 'notistack';
import styled from 'styled-components';

///STYLE
const Container = styled.section`
  display: inline-block;
  align-self: center;
  align-items: center;
  padding: 20px 60px 50px 60px;
  background: white;
  box-shadow: 0 14px 28px rgba(0, 0, 0, 0.25), 0 10px 10px rgba(0, 0, 0, 0.22);
`;

const Title = styled.h1`
  margin-bottom: 60px;
  font-size: 30px;
  font-family: 'Officina Serif' !important;
  font-weight: 700 !important;
  color: #120338;
`;

const CentrForm = styled.section`
  display: flex;
`;

const sleep = (time) => new Promise((acc) => setTimeout(acc, time));

interface FormValues {
  phoneNumber: string | number;
  price: string;
}

const initialValues: FormValues = {
  phoneNumber: '',
  price: '',
};

interface State {
  textmask: string;
}

const PaymentSchema = Yup.object().shape({
  phoneNumber: Yup.string().required('Введите номер'),
  price: Yup.number()
    .min(1, '')
    .max(1000, 'Сумма не должна превышать 1000₽')
    .required('Введите сумму платежа'),
});

//Компонент
const NewInput: React.FC = (props) => {
  const successMsg = [
    'Оплата мобильной связи прошла успешно!',
    'Произошла ошибка! Повторите операцию',
  ];

  const [isLoading, setLoading] = useState(false);

  const [open, setOpen] = React.useState(false);

  const handleClick = () => {
    setOpen(true);
  };

  const handleClose = (event?: React.SyntheticEvent, reason?: string) => {
    if (reason === 'clickaway') {
      return;
    }

    setOpen(false);
  };

  const handleSubmit = async (values: FormValues, formikHelpers: FormikHelpers<FormValues>) => {
    setLoading(true);
    await sleep(3000);
    setLoading(false);
    await sleep(1);
    alert(successMsg[Math.floor(Math.random() * successMsg.length)]);
  };

  const [values, setValues] = React.useState<State>({
    textmask: '',
  });

  const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setValues({
      ...values,
      [event.target.name]: event.target.value,
    });
  };

  return (
    <motion.div
      className="centrForm"
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ delay: 0.3 }}>
      <Container>
        <div>
          <Title>Пополнить счёт</Title>
        </div>
        <Formik
          initialValues={initialValues}
          onSubmit={handleSubmit}
          validationSchema={PaymentSchema}
          handleChange={handleChange}
          isSubmitting
          // component={MyForm}
          dirty>
          {(props) => {
            // props.isSubmitting;
            return (
              <Form>
                <div>
                  <MyMaskField
                    variant="outlined"
                    label="Номер телефона"
                    textmask=""
                    name="phoneNumber"
                    id="phoneNumber"
                    type="string"
                    required
                  />
                  <MyTextField
                    variant="outlined"
                    label="Сумма платежа"
                    name="price"
                    id="price"
                    type="number"
                    required
                  />
                </div>

                <div>
                  {!isLoading && (
                    <Button variant="outlined" type="submit">
                      Оплатить
                    </Button>
                  )}
                  {isLoading && (
                    <Button variant="outlined" type="submit" disabled>
                      Оплата...
                      <CircularProgress color="#6200ea" />
                    </Button>
                  )}
                </div>
              </Form>
            );
          }}
        </Formik>
      </Container>
    </motion.div>
  );
};

export default NewInput;
