import React, { useState } from 'react';
import { Formik, Form, FormikHelpers } from 'formik';
import * as Yup from 'yup';
import MyTextField from './use';
import MyMaskField from './use.Mask';
// import styled from 'styled-components';
import { motion } from 'framer-motion';
import Button from '@material-ui/core/Button';
import { CircularProgress } from 'material-ui';

// const Button = styled.button`
//   border: none;
//   background-color: #1f2229;
//   font-family: 'AvenirNextCyrRegular', sans-serif;
//   font-size: 18px;
//   transition: all 0.15s;
//   cursor: pointer;
//   width: 250px;
//   height: 60px;
//   border-radius: 2px;
//   color: #fff;
//   margin: 12px 0;
//   &:hover {
//     background: rgba(0, 0, 0, 0);
//     color: #000000;
//     box-shadow: inset 0 0 0 3px #000000;
//   }
// `;

const sleep = (time) => new Promise((acc) => setTimeout(acc, time));

interface FormValues {
  phoneNumber: string | number;
  price: string;
  nam: string;
}

const initialValues: FormValues = {
  phoneNumber: '',
  price: '',
  nam: '',
};

interface State {
  textmask: string;
}

const PaymentSchema = Yup.object().shape({
  phoneNumber: Yup.string().required('Введите номер'),
  price: Yup.number()
    .min(1, '')
    .max(1000, 'Сумма не должна превышать 1000₽')
    .required('Введите сумму платежа'),
});

//Компонент
const NewInput: React.FC = (props) => {
  const successMsg = [
    'Оплата мобильной связи прошла успешно!',
    'Произошла ошибка! Повторите операцию',
  ];

  const [isLoading, setLoading] = useState(false);

  const handleSubmit = async (values: FormValues, formikHelpers: FormikHelpers<FormValues>) => {
    setLoading(true);
    await sleep(3000);
    setLoading(false);
    await sleep(1);
    alert(successMsg[Math.floor(Math.random() * successMsg.length)]);
  };

  const [values, setValues] = React.useState<State>({
    textmask: '',
  });

  const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setValues({
      ...values,
      [event.target.name]: event.target.value,
    });
  };

  return (
    <motion.div
      className="centrForm"
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ delay: 0.3 }}>
      <div className="container">
        <h2 className="title">Пополнить счёт</h2>
        <Formik
          initialValues={initialValues}
          onSubmit={handleSubmit}
          validationSchema={PaymentSchema}
          handleChange={handleChange}
          // component={MyForm}
          dirty>
          {(props) => {
            // props.isSubmitting;
            return (
              <Form>
                <div>
                  <MyMaskField
                    variant="outlined"
                    label="Номер телефона"
                    textmask=""
                    name="phoneNumber"
                    id="phoneNumber"
                    type="string"
                    required
                  />
                  <MyTextField
                    variant="outlined"
                    label="Сумма платежа"
                    name="price"
                    id="price"
                    type="number"
                    required
                  />
                </div>
                <div>
                  {!isLoading && (
                    <Button variant="outlined" type="submit">
                      Оплатить
                    </Button>
                  )}
                  {isLoading && (
                    <Button variant="outlined" type="submit" disabled>
                      Оплата...
                      <CircularProgress color="#6200ea" />
                    </Button>
                  )}
                </div>
              </Form>
            );
          }}
        </Formik>
      </div>
    </motion.div>
  );
};

export default NewInput;
