import React, { useState } from 'react';
import { Formik, Form, Field } from 'formik';
import * as Yup from 'yup';
import MyTextField from './use';
import MaskedInput from 'react-text-mask';
import MyMaskField from './use.Mask';
import styled from 'styled-components';

const Button = styled.button`
  border: none;
  background-color: #1f2229;
  font-family: 'AvenirNextCyrRegular', sans-serif;
  font-size: 18px;
  transition: all 0.15s;
  cursor: pointer;
  width: 250px;
  height: 60px;
  border-radius: 2px;
  color: #fff;
  margin: 12px 0;
  &:hover {
    background: rgba(0, 0, 0, 0);
    color: #000000;
    box-shadow: inset 0 0 0 3px #000000;
  }
`;

interface FormValues {
  phoneNumber: string | number;
  price: string;
  nam: string;
}

const initialValues: FormValues = {
  phoneNumber: '',
  price: '',
  nam: '',
};

interface TextMaskCustomProps {
  inputRef: (ref: HTMLInputElement | null) => void;
}

//Компонент
const TextMaskCustom = (props: TextMaskCustomProps) => {
  const { inputRef, ...other } = props;

  return (
    <MaskedInput
      {...other}
      ref={(ref: any) => {
        inputRef(ref ? ref.inputElement : null);
      }}
      mask={[
        '+',
        '7',
        ' ',
        '(',
        /[7-9]/,
        /\d/,
        /\d/,
        ')',
        ' ',
        /\d/,
        /\d/,
        '-',
        /\d/,
        /\d/,
        '-',
        /\d/,
        /\d/,
        /\d/,
      ]}
      placeholderChar={'\u2000'}
      showMask
    />
  );
};

interface State {
  textmask: string;
}

const PaymentSchema = Yup.object().shape({
  phoneNumber: Yup.string().required('Введите номер').length(18, 'error'),
  price: Yup.number()
    .min(1, '')
    .max(1000, 'Сумма не должна превышать 1000₽')
    .required('Введите сумму платежа'),
});

//Компонент
const NewInput: React.FC = (props) => {
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [modalIsOpe, setModalIsOpen1] = useState(false);

  const successMsg = [
    'Оплата мобильной связи прошла успешно!',
    'Произошла ошибка! Повторите операцию',
  ];

  const handleSubmit = (values: FormValues): void => {
    return alert(successMsg[Math.floor(Math.random() * successMsg.length)]);
  };

  const [values, setValues] = React.useState<State>({
    textmask: '',
  });
  const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setValues({
      ...values,
      [event.target.name]: event.target.value,
    });
  };
  return (
    <div className="container">
      <h2 className="title">Пополнить счёт</h2>

      <Formik
        initialValues={initialValues}
        onSubmit={handleSubmit}
        validationSchema={PaymentSchema}
        handleChange={handleChange}
        dirty>
        {() => {
          return (
            <Form>
              <MyMaskField
                label="Номер телефона"
                textmask=""
                name="phoneNumber"
                id="phoneNumber"
                type="string"
                required
              />
              <MyTextField label="Сумма платежа" name="price" id="price" type="number" required />
              <div>
                <Button type="submit">ОПЛАТИТЬ</Button>
              </div>
            </Form>
          );
        }}
      </Formik>
    </div>
  );
};

export default NewInput;
