import React, { Component, useState } from 'react';
import { CircularProgress } from 'material-ui';

// const useSpinner = () => {
//   const [loading, setLoading] = useState(false);
//   return [loading ? <CircularProgress /> : null, () => setLoading(true), () => setLoading(false)];
// };

// export default useSpinner;

const UseSpinner: React.FC = () => {
  const [isLoading, setLoading] = useState(false);

  const spinner = () => {
    setLoading(true);

    fetch('https://jsonplaceholder.typicode.com/todos/1')
      .then((response) => response.json())
      .then((json) => {
        console.log(json);
        setLoading(false);
      });
  };

  return (
    <div>
      {!isLoading && <button onClick={spinner}>Click</button>}
      {isLoading && (
        <button disabled>
          <CircularProgress />
          Click
        </button>
      )}
    </div>
  );
};

export default UseSpinner;
