import React from 'react';
import { useField } from 'formik';
import TextField from '@material-ui/core/TextField';
import MaskedInput from 'react-input-mask';

interface Values {
  name: string;
  type?: string;
  required?: boolean;
  id: string;
  textmask: string;
  label: string;
  variant: any;
}

interface TextMaskCustomProps {
  inputRef: (ref: HTMLInputElement | null) => void;
}

//Компонент
const TextMaskCustom = (props: TextMaskCustomProps) => {
  const { inputRef, ...other } = props;

  return <MaskedInput mask="+7 (999) 999 99 99" id="phone" type="text" />;
};

const MyMaskField: React.FC<Values> = ({ id, name, variant, type, label }) => {
  const [field, meta] = useField(name);
  // console.log(meta.error, field.value);
  return (
    <div className="form2">
      <TextField
        label={label}
        value={field.value}
        onChange={field.onChange}
        autoComplete="off"
        id={id}
        name={name}
        type={type}
        variant={variant}
        InputProps={{
          inputComponent: TextMaskCustom as any,
        }}
        error={!!meta.error}
      />
      {meta.touched && meta.error ? <div className="textErrror">{meta.error}</div> : null}
    </div>
  );
};

export default MyMaskField;
