//   const { inputRef, ...other } = props;

//   return (
//     <MaskedInput
//       {...other}
//       ref={(ref: any) => {
//         inputRef(ref ? ref.inputElement : null);
//       }}
//       mask={[
//         '+',
//         '7',
//         ' ',
//         '(',
//         /[7-9]/,
//         /\d/,
//         /\d/,
//         ')',
//         ' ',
//         /\d/,
//         /\d/,
//         '-',
//         /\d/,
//         /\d/,
//         '-',
//         /\d/,
//         /\d/,
//         /\d/,
//       ]}
//       placeholderChar={'\u2000'}
//       showMask
//     />
//   );
// };

interface State {
  textmask: string;
}

const PaymentSchema = Yup.object().shape({
  phoneNumber: Yup.string().required('Введите номер').length(18, 'error'),
  price: Yup.number()
    .min(1, '')
    .max(1000, 'Сумма не должна превышать 1000₽')
    .required('Введите сумму платежа'),
});

//Компонент
const NewInput: React.FC = (props) => {
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [modalIsOpe, setModalIsOpen1] = useState(false);

  const successMsg = [
    'Оплата мобильной связи прошла успешно!',
    'Произошла ошибка! Повторите операцию',
  ];

  // const arrModal = [setModalIsOpen(true), setModalIsOpen1(true)];

  const handleSubmit = (values: FormValues): void => {
    return alert(successMsg[Math.floor(Math.random() * successMsg.length)]);
  };

  const [values, setValues] = React.useState<State>({
    textmask: '',
  });
  const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setValues({
      ...values,
      [event.target.name]: event.target.value,
    });
  };
  return (
    <div className="container">
      <h2 className="title">Пополнить счёт</h2>

      <Formik
        initialValues={initialValues}
        onSubmit={handleSubmit}
        validationSchema={PaymentSchema}
        handleChange={handleChange}
        dirty>
        {() => {
          return (
            <Form>
              <MyMaskField
                label="Номер телефона"
                textmask=""
                name="phoneNumber"
                id="phoneNumber"
                type="string"
                required
              />
              <MyTextField label="Сумма платежа" name="price" id="price" type="number" required />
              <div>
                <Button type="submit">ОПЛАТИТЬ</Button>
                <Modal
                  isOpen={modalIsOpen}
                  shouldCloseOnOverlayClick={false}
                  onRequestClose={() => setModalIsOpen(false)}
                  style={{
                    overlay: {
                      backgroundColor: 'grey',
                      position: 'fixed',
                    },
                    content: {
                      position: 'absolute',
                      top: '400px',
                      left: '400px',
                      right: '400px',
                      bottom: '400px',
                      border: '1px solid #ccc',
                      background: '#fff',
                      overflow: 'auto',
                      WebkitOverflowScrolling: 'touch',
                      borderRadius: '4px',
                      outline: 'none',
                      padding: '200px',
                    },
                  }}>
                  <h2>title</h2>
                  <p>Операция прошла успшно!</p>
                  <div>
                    <button onClick={() => setModalIsOpen(false)}>Close</button>
                  </div>
                </Modal>
              </div>
              <div>
                <Modal
                  isOpen={modalIsOpe}
                  shouldCloseOnOverlayClick={false}
                  onRequestClose={() => setModalIsOpen1(false)}
                  style={{
                    overlay: {
                      backgroundColor: 'grey',
                      position: 'fixed',
                    },
                    content: {
                      position: 'absolute',
                      top: '400px',
                      left: '400px',
                      right: '400px',
                      bottom: '400px',
                      border: '1px solid #ccc',
                      background: '#fff',
                      overflow: 'auto',
                      WebkitOverflowScrolling: 'touch',
                      borderRadius: '4px',
                      outline: 'none',
                      padding: '200px',
                    },
                  }}>
                  <h2>title</h2>
                  <p>Произошла ошибка, повторите операцию!</p>
                  <div>
                    <button onClick={() => setModalIsOpen1(false)}>Close</button>
                  </div>
                </Modal>
              </div>
            </Form>
          );
        }}
      </Formik>
    </div>
  );
};

export default NewInput;
